mapper.select:
SELECT id, content, writer, title, view_cnt, date_format(reg_date, '%y-%m-%d') as reg_date
FROM board
WHERE id = #{id};

mapper.update:
UPDATE board
SET view_cnt = view_cnt+1
WHERE id = #{id}

UPDATE board
SET title = #{title}, content=#{content}, reg_date = now()
WHERE id=#{id}

mapper.select:
SELECT id, content, writer, title, view_cnt as viewCnt, date_format(reg_date, '%y-%m-%d %H:%i:%s') as regDate FROM board
WHERE ${key} LIKE concat('%', #{word}, '%')
ORDER BY ${orderBy} ${orderByDir}
